-- ----------------------------
-- Table structure for `ob_mg_game`
-- ----------------------------
DROP TABLE IF EXISTS `ob_mg_game`;
CREATE TABLE `ob_mg_game` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `game_category_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '游戏分类id',
  `game_name` varchar(100) COLLATE utf8_unicode_ci NOT NULL DEFAULT '' COMMENT '游戏名称',
  `game_intro` varchar(250) COLLATE utf8_unicode_ci NOT NULL DEFAULT '' COMMENT '游戏简介',
  `game_cover` varchar(255) COLLATE utf8_unicode_ci NOT NULL DEFAULT '' COMMENT '游戏封面',
  `game_head` varchar(255) COLLATE utf8_unicode_ci NOT NULL DEFAULT '' COMMENT '游戏头像图片',
  `game_type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0:H5   1:安卓   2:IOS',
  `game_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '游戏ID',
  `version` char(20) COLLATE utf8_unicode_ci NOT NULL DEFAULT '' COMMENT '版本号',
  `describe_text` text COLLATE utf8_unicode_ci NOT NULL COMMENT '富文本描述',
  `is_recommend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `is_hot` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否热门',
  `download_url` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT '' COMMENT '游戏开始URL 或 游戏包下载URL',
  `sort` int(10) unsigned NOT NULL DEFAULT '0',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  `update_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `create_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=99 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='手游表';

