  `brief` varchar(50) NOT NULL DEFAULT '' COMMENT '简介',
  `number` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '剩余数量',
  `gift_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '源数据礼包ID',
  `logo` varchar(300) NOT NULL DEFAULT '' COMMENT '游戏logo',
  `use_number` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '使用数量',
  `create_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `status` tinyint(2) NOT NULL DEFAULT '1' COMMENT '状态：0禁用，1启用',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=279 DEFAULT CHARSET=utf8 COMMENT='手游礼包表';

